import React, { useState } from 'react'
import "./hotel.css"
import Header from '../components/header/Header'
import Navbar from "../components/navbar/Navbar"
import { faCircleArrowLeft, faCircleArrowRight, faCircleXmark, faLocationDot } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Footer from "../components/footer/Footer"
import MailList from "../components/mailList/MailLIst"
const Hotel = () => {
    const [sliderNumber, setSliderNumber] = useState(0)
    const [open, setOpen] = useState(false)
    const photos = [
        {
            src: "data:image/jpeg;base64,"
        },
        {
            src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTd1Urz7laY0sLLSntG9jpSvhSxWbkWpj0sgw&usqp=CAU"
        },
        {
            src: "https://i.pinimg.com/originals/91/ba/b1/91bab1cf01d76e1cdf90fad36f1c3ccc.jpg"
        },
        {
            src: "data:image/jpeg;base64,"
        },
        {
            src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGDBsAEl32hPTxGWZfHBnN_5WfuQRyVgPYeQ&usqp=CAU"
        },
        {
            src: "data:image/jpeg;base64,"
        },
    ]
    const handleOpen = (i) => {
        setSliderNumber(i)
        setOpen(true)
    }
    const handleMove = (direction) => {
        let newSliderNumber;
        if (direction === "l") {
            newSliderNumber = sliderNumber === 0 ? 5 : sliderNumber - 1
        } else {
            newSliderNumber = sliderNumber === 5 ? 0 : sliderNumber + 1

        }
        setSliderNumber(newSliderNumber)
    }
    return (
        <div>
            <Navbar />
            <Header type="list" />
            <div className="hotelContainer">
                {open && <div className="slired">
                    <FontAwesomeIcon icon={faCircleXmark} className="close" onClick={() => setOpen(false)} />
                    <FontAwesomeIcon icon={faCircleArrowLeft} className="arrow" onClick={() => handleMove("l")} />
                    <div className="sliderWrapper">
                        <img src={photos[sliderNumber].src} alt="" className="sliderImg" />
                    </div>
                    <FontAwesomeIcon icon={faCircleArrowRight} className="arrow" onClick={() => handleMove("r")} />
                </div>}
                <div className="hotelWrapper">
                    <button className='bookNow' >
                        Reserve or Book now!
                    </button>
                    <h1 className="hotelTitle">Grand Hotel</h1>
                    <div className="hotelAddress">
                        <FontAwesomeIcon icon={faLocationDot} />
                        <span>Gorod st 123 Qo'qon</span>
                    </div>
                    <span className="hotelDistance">
                        Excellent location - 1000m from center
                    </span>
                    <span className="hotelPriceHighlight">
                        Nimadir
                    </span>
                    <div className="hotelImages">
                        {photos.map((photo, i) => (
                            <div className="hotelImgWrapper">
                                <img onClick={() => handleOpen(i)} src={photo.src} className="hotelImg" alt="" />
                            </div>
                        ))}
                    </div>
                    <div className="hotelDetails">
                        <div className="hotelDetailsTexts">
                            <h1 className="hotelTitle">stay ib the heart of Crakow</h1>
                            <p className="hotelDesc">
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. Deleniti commodi ea officia hic fugit eaque inventore tempora. Quibusdam minus nesciunt earum eos! Tempora expedita excepturi dolore maxime laborum corporis rem nostrum eum sequi repellendus. Sint natus pariatur aperiam officia, suscipit mollitia sed repellendus at iure, corrupti facere, illo ratione repellat sapiente nesciunt odit laudantium sunt ut reiciendis magnam commodi dolor inventore. Quo modi eaque obcaecati ipsa explicabo nulla accusamus vitae alias vero, est sunt! Unde ea voluptatum delectus architecto doloribus voluptatibus fuga consectetur quisquam possimus quis mollitia magnam omnis aperiam, iusto perferendis adipisci autem velit numquam quas minus aspernatur vel.
                            </p>
                        </div>
                        <div className="hotelDetailsPrice">
                            <h1>Perfect for a 9-night stay!</h1>
                            <span>
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. Facilis, ad!
                            </span>
                            <h2>
                                <b>$855</b> (9 night)
                            </h2>
                            <button>Reserve or Book now!</button>
                        </div>
                    </div>
                </div>
            </div>
            <MailList />
            <Footer />
        </div>
    )
}

export default Hotel